import "./Mascotas.css";
import Pelicula from "../Mascota/Mascota";
import { useEffect, useState, useContext } from "react";
import { UserContext } from "../../contexts/UserContext";

function Mascotas() {
  const [mascotas, setMascotas] = useState([]);

  const { user } = useContext(UserContext);
  const isUserLoggedIn = user ? true : false;
  console.log("isUserLoggedIn", isUserLoggedIn);

  const cargarMascotas = () => {
    fetch("http://localhost:3000/json/mascotas.json")
      .then((respuesta) => respuesta.json())
      .then((data) => setMascotas(data));
  };

  useEffect(() => {
    cargarMascotas();
  }, []);

  return (
    <section id="mascotas">
      <div className="container">
        <h1>Mascotas</h1>
        {isUserLoggedIn ? (
          <button className="btn btn-dark agregar-mascota">
            Agregar mascota
          </button>
        ) : null}
        <div className="grid-mascotas">
          {mascotas.map((mascota) => (
            <Mascota key={mascota.id} data={mascota}></Mascota>
          ))}
        </div>
      </div>
    </section>
  );
}

export default Mascotas;
